import { Controller, Get, HttpCode } from "@core/common";
import { HttpStatus } from "@core/common/enums";
import express from 'express'
import { SearchService } from "./search.service";
@Controller('search')
export class SearchEngineController {
  searchService: SearchService
  constructor() {
    this.searchService = new SearchService()
  }

  @Get(':text')
  async find(req: express.Request) {
    const text = req.params.text
    return this.searchService.findPages(text)
  }

  @Get('byId/:id')
  @HttpCode(HttpStatus.ACCEPTED)
  async findOne(req: express.Request) {
    const id = parseInt(req.params.id)
    return this.searchService.findById(id)
  }
}

export function Module(metadata: any): ClassDecorator {
  return (target: Function) => {
    for (const property in metadata) {
      if (metadata.hasOwnProperty(property)) {
        Reflect.defineMetadata(property, (metadata as any)[property], target);
      }
    }
  };
}

import { PATH_METADATA } from "../constants";

export function Controller(path: any): ClassDecorator {

  return (target: Function) => {
    Reflect.defineMetadata(PATH_METADATA, path, target);
  }
}

export const MODULE_METADATA = {
  IMPORTS: 'imports',
  CONTROLLERS: 'controllers',
  PATH: 'path',
};

export const PATH_METADATA = 'path';
export const METHOD_METADATA = 'method';
export const HTTP_CODE_METADATA = 'http_code';

import { METHOD_METADATA, PATH_METADATA } from '../constants';
import { RequestMethod } from '../enums/request-method.enum';

export interface RequestMappingMetadata {
  path?: string | string[];
  method?: RequestMethod;
}

const defaultMetadata = {
  [PATH_METADATA]: '/',
  [METHOD_METADATA]: RequestMethod.GET,
};

export const RequestMapping = (
  metadata: RequestMappingMetadata = defaultMetadata,
): MethodDecorator => {
  const pathMetadata = metadata[PATH_METADATA];
  const path = pathMetadata && pathMetadata.length ? pathMetadata : '/';
  const requestMethod = metadata[METHOD_METADATA] || RequestMethod.GET;


  return (
    target: object,
    key: string | symbol,
    descriptor: TypedPropertyDescriptor<any>,
    ) => {
    Reflect.defineMetadata(PATH_METADATA, path, descriptor.value);
    Reflect.defineMetadata(METHOD_METADATA, requestMethod, descriptor.value);
    return descriptor;
  };
};

const createMappingDecorator =
  (method: RequestMethod) =>
  (path?: string | string[]): MethodDecorator => {
    return RequestMapping({
      [PATH_METADATA]: path,
      [METHOD_METADATA]: method,
    });
  };

export const Post = createMappingDecorator(RequestMethod.POST);
export const Get = createMappingDecorator(RequestMethod.GET);
export const Delete = createMappingDecorator(RequestMethod.DELETE);
export const Put = createMappingDecorator(RequestMethod.PUT);
export const Patch = createMappingDecorator(RequestMethod.PATCH);
export const Options = createMappingDecorator(RequestMethod.OPTIONS);
export const Head = createMappingDecorator(RequestMethod.HEAD);
export const All = createMappingDecorator(RequestMethod.ALL);

import { HTTP_CODE_METADATA } from '../constants';

export function HttpCode(statusCode: number): MethodDecorator {
  return (
    target: object,
    key: string | symbol,
    descriptor: TypedPropertyDescriptor<any>,
  ) => {
    Reflect.defineMetadata(HTTP_CODE_METADATA, statusCode, descriptor.value);
    return descriptor;
  };
}

import { Db } from "src/adapters";

export class SearchService {
  page: any
  constructor() {
    this.page = Db.page
  }

  findStrategy(text: string) {
    // incomplete
    return {
      contains: text,
      mode: 'insensitive',
    }
  }

  async findPages(text: string) {
    const pages = await this.page.findMany(
      {
        where: {
          title: this.findStrategy(text)
        }
      }
    )

    return pages
  }

  async findById(id: number) {
    const result = await this.page.findUnique({
      where: {
        id,
      },
    })
    return result
  }
}

import { PRODUCTION } from '@core/common/constants.env'
import { nextConnector } from '@front'
import express from 'express'

export const nextAdapter = async (): Promise<express.Application> => {
  const app = express()
  const nextApp = await nextConnector({ dev: !PRODUCTION, dir:'./front' })
  const handle = nextApp.getRequestHandler()

  app.all(/^(?!(\/api\/))/, (req, res) => {
    return handle(req, res)
  })

  return app
}

import next from 'next'
import { NextServer, NextServerOptions } from 'next/dist/server/next'
import conf from './next.config'


export const nextConnector = async (options: NextServerOptions): Promise<NextServer> => {
  options.conf = {
    ...conf,
    ...options.conf,
    basePath: conf?.publicRuntimeConfig?.basePath
  }
  const app = Object.create(next(options))
  await app.prepare()
  return app
}

/** @type {import('next').NextConfig} */
const nextConfig = {
  reactStrictMode: true,
  distDir: '.next',
  swcMinify: true,
  publicRuntimeConfig: {
    // Will only be available on the server side
    appName: process.env.APP_NAME || 'My app',
    appihost: process.env.API_HOST || '/',
  }
}

module.exports = nextConfig

import { PrismaClient } from '@prisma/client'

const prisma = new PrismaClient()

export const Db = prisma
